const Category = ({ category }) => {
  return (
    <div>
      <h2>{category.name}</h2>
      {/* List de produits */}
      {category.meals.map((meal, index) => {
        return (
          <div
            onClick={() => {
              console.log("J'ai cliqué sur le produit ====> ", meal);
            }}
            className="MenuItems--items"
          >
            <div class="MenuItem">
              <div class="MenuItem--card">
                <div class="MenuItem--texts"></div>
                <h3>{meal.title}</h3>
                <p>{meal.description.slice(0, 50)}...</p>

                <div class="MenuItem--infos">
                  <span class="MenuItem--price">{meal.price} €</span>
                  <span class="MenuItem--popular">
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      viewBox="0 0 24 24"
                      fill="#ff8000"
                      class="feather feather-star"
                      style="width: 20px; height: 20px; margin-right: 5px"
                    >
                      <polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon>
                    </svg>
                    Populaire
                  </span>
                </div>

                <span className="">{meal.price} €</span>
                {meal.popular && <span>★ Populaire</span>}

                {meal.picture && (
                  <div class="MenuItem--picture">
                    <img style={{ height: 100 }} src={meal.picture} alt="" />
                  </div>
                )}
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Category;
